operations.query:
query CovariantInterfaceImplementation {
	randomItem {
		__typename
		id
		next {
			__typename
			... ContentFields
		}
		related {
			__typename
			... ContentFields
		}
	}
	root {
		... ContentFields
		... TopicFields
		next {
			... TopicFields
		}
		related {
			... TopicFields
		}
	}
}
fragment ContentFields on Content {
	next {
		__typename
		id
	}
	related {
		__typename
		id
	}
}
fragment TopicFields on Topic {
	next {
		id
	}
	related {
		id
	}
}
